mapper.select:
SELECT 
	COUNT(*) 
FROM 
	item_info

	1=1

		AND ITEM_NM LIKE concat('%', #{keyword}, '%')


		AND ITEM_MID_CATEGORY_CD = #{category}


		AND ITEM_DYE_YN = #{dyeYN}


		AND ITEM_GET_FROM_CD = #{getFrom}



	;


		SELECT 
			ITEM_NO,
			ITEM_NM, 
			ITEM_IMG_AFT,
			ITEM_GET_FROM_DT
		FROM 
			item_info 

			1=1

            	AND ITEM_NM like concat('%', #{keyword}, '%')


				AND ITEM_MID_CATEGORY_CD = #{category}


				AND ITEM_DYE_YN = #{dyeYN}


				AND ITEM_GET_FROM_CD = #{getFrom}



			LIMIT #{pagination.limitStart}, #{recordSize};
